  function neue_slider() {

        my_slider_counter = 0;
        curr_slide = 0;
        //nav_slider_counter = 0;

        //$(".slide_nav_cont ul").empty();
        $('#slideshow img').each(function() {
            $(this).addClass('slide_' + my_slider_counter);
            my_slider_counter++;

           /*  if(my_slider_counter == 1)
               $('.slide_nav_cont ul').append('<li class="current"></li>');
             else
              $('.slide_nav_cont ul').append('<li></li>');*/
        });

	/*
        $('.slide_nav_cont ul li').each(function() {
            $(this).attr('alt',nav_slider_counter);
            nav_slider_counter++;
        });                */

        function home_switch_slide() {

            if(curr_slide >= my_slider_counter)
                curr_slide = 0;
            else if(curr_slide < 0)
                curr_slide = (my_slider_counter-1);

            //alert(curr_slide);

            $('.slide_' + curr_slide).fadeIn(500);
            //$('.slide_nav_cont ul li').removeClass('current');
            //$('.slide_nav_cont ul li[alt="' + curr_slide + '"]').addClass('current');


        }

        function hide_curr_slide() {
               $('.slide_' + curr_slide).stop();
               $('.slide_' + curr_slide).fadeOut(1500);
        }

        function next_slide_slider(jump_to_slide) {
            hide_curr_slide();

            if(jump_to_slide == null)
                curr_slide++;
            else
                curr_slide = jump_to_slide;

            t_slide=setTimeout(home_switch_slide,0);
            //home_switch_image();
        }

        function prev_slide_slider(jump_to_slide) {
            hide_curr_slide();


//            curr_slide--;
            if(jump_to_slide == null)
                curr_slide--;
            else
                curr_slide = jump_to_slide;
            //home_switch_image();
            t_slide=setTimeout(home_switch_slide,0);
        }

        $('.slide_prev').click(function() {

            prev_slide_slider();
            clearInterval(intervalID_slide);
            //clearInterval(t_slide);
            intervalID_slide = setInterval(next_slide_slider, 8000);
        });

        $('.slide_next').click(function() {

            next_slide_slider();
            clearInterval(intervalID_slide);
            intervalID_slide = setInterval(next_slide_slider, 8000);
        });
        /*
        $('.slide_nav_cont ul li').click(function() {

            clearInterval(intervalID_slide);
            next_slide_slider($(this).attr('alt'));

            //clearInterval(t_testi);
            intervalID_slide = setInterval(next_slide_slider, 8000);

        });        */

        //setInterval(next_slide_image, 5000);
        intervalID_slide = setInterval(next_slide_slider, 8000);


  }
$(document).ready(function() {
	neue_slider();

        $('.header_menu li').hover(
            function () {
                $('ul:first', this).css('display','block');

            },
            function () {
                $('ul:first', this).css('display','none');
            }
        );
});
$(document).ready(function() {
	neue_slider();

	$('.home_post_box').hover(
// Old style.
//		function() {
//			$(this).find('.home_post_text').css('display','block');
//		},
//		function () {
//			$(this).find('.home_post_text').css('display','none');
//		}
// New style (fading).
		function(){
			$(this).find('.home_post_text').fadeIn(250);
	},
		function(){
			$(this).find('.home_post_text').fadeOut(250);
	}
	);

	$('#slideshow_cont').hover(
		function() {
			$('.slide_prev').css('display','block');
			$('.slide_next').css('display','block');
		},
		function() {
			$('.slide_prev').css('display','none');
			$('.slide_next').css('display','none');
		}
	);
});



